import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss'],
})
export class EventosComponent implements OnInit {
  public eventos: any;

  constructor(private http: HttpClient) {


  }

  ngOnInit(): void {
    this.getEventos()
  }

  public getEventos(): void {
    this.http.get('https://localhost:5001/api/eventos').subscribe(
      response => this.eventos = response,
      error => console.log(error),
    );

  }
}
<h1>eventos works!</h1>

<div *ngFor="let item of eventos">
  <p><b>Tema: </b>{{item.tema}}</p>
  <p><b>Local: </b>{{item.local}}</p>
  <p><b>Data do Evento: </b>{{item.dataEvento}}</p>
  <p><b>Quantidade de pessoas: </b>{{item.qtdPessoas}}</p>
  <p><b>Lote: </b>{{item.lote}}</p>
  <p><b>Imagem: </b>{{item.imagemURL}}</p>
  <hr>
</div>
